INSERT INTO tourscanner.tour (common_cash_description, common_cash_included_services, duration, included_services, itinerary, nation_id, not_included_services, title, price, company_id, link_to_tour, id) VALUES ('50 €', '', '8 giorni', 'Polizza infortuni e Polizza Europassistance, 7 notti in camera doppia (eccezionalmente tripla) con bagno (chi desiderasse la stanza singola, in caso di disponibilità, dovrà pagare la differenza di 8 euro al giorno, direttamente in albergo). Il trattamento è di mezza pensione (comprese le bevande) presso l''Hotel Rosa (***) a Vigo di Fassa, 3 ore di scuola di sci per 5 giorni, assistenza del coordinatore maestro di sci durante il soggiorno.Qualora per motivi legati a condimeteo o nivologiche avverse il maestro dovesse decidere di non effettuare la lezione per quel giorno, sarà rimborsata in loco metà quota giornaliera di lezione.', 'QUATTROPASSISKIUna settimana di sci in Val di Fassa, 5 giorni di scuola e 6/7 giorni di sci di cui uno certamente dedicato al mitico "Giro dei 4 Passi", toccando le quattro valli (Livinallongo, Badia, Gardena e Fassa.)', 1, 'I pasti al di fuori della mezza a pensione in albergo (il pranzo si fa generalmente al sacco o in rifugi lungo le piste) e tutto ciò che non è indicato ne LA QUOTA COMPRENDE. Non comprende inoltre tutti i trasporti (compreso lo skipass). A questo proposito sarà opportuno decidere  in loco se sarà conveniente fare lo skipass settimanale (comprendente o meno il SUPERSKIDOLOMITI) o fare un giornaliero di volta in volta.Le tariffe dello skipass del territorio possono essere consultate al link www.fassa.com/IT/Prezzi-Skipass/ ', 'SELLARONDASKI', '590 €', 3, 'http://www.viaggiavventurenelmondo.it/nuovosito/viaggi/schedeviaggi/6720.php', 7140);
INSERT INTO tourscanner.tour (common_cash_description, common_cash_included_services, duration, included_services, itinerary, nation_id, not_included_services, title, price, company_id, link_to_tour, id) VALUES ('50 €', '', '08 giorni', 'Polizza infortuni e polizza Europassistance, 7 notti in camera doppia (eccezionalmente tripla) con bagno (chi desiderasse la stanza singola, in caso di disponibilità, dovrà pagare la differenza, € 8/notte, direttamente in albergo) e con trattamento di mezza pensione presso l’Hotel Monte Emilius (***) a Charvensod (bevande escluse), 3 ore di scuola di sci per 5 giorni, assistenza del coordinatore Maestro di sci durante il soggiorno.Qualora per motivi legati a condimeteo o nivologiche avverse il maestro dovesse decidere di non effettuare la lezione per quel giorno, sarà rimborsata in loco metà quota giornaliera di lezione.', 'Una settimana di sci al Monte Bianco, 5 giorni di scuola e 6/7 di sci   di cui uno, possibilmente, dedicato alla discesa della Vallée Blanche (Mer de Glace) da Punta Helbronner fino a Chamonix CON LA NUOVA FAVOLOSA FUNIVIA DEL MONTEBIANCO INAUGURATA NEL 2015', 1, 'I pranzi o cene al di fuori della mezza pensione (il pranzo si fa generalmente al sacco o in rifugi lungo le piste) e tutto ciò che non è indicato ne “LA QUOTA COMPRENDE”.  INOLTRE LA QUOTA NON COMPRENDE LE SEGUENTI ATTIVITA’ FACOLTATIVE: • Traversata della Mer de Glace: - Diaria dellaGuida Alpina (da ripartirsi fra i partecipanti - massimo 8 per guida); - Pulmino da Chamonix a Courmayeur   - Trenino (eventuale e solo nel caso di scarso innevamento) dalla base della mer de Glace a Chamonix  - Skipass: a questo proposito la spesa può essere variabile. Si può scegliere tra  lo skipass    valido per tutta la Val d’Aosta, che comprende anche la risalita per la traversata della Mer de Glace, o l’acquisto di volta in volta di un giornaliero nelle varie località. Le tariffe   si possono consultare a questo indirizzo http://www.skilife.ski/valle-daosta/skipass-valle-daosta/ . Per vedere la spesa massima che si può sostenere per la traversata della Vallée Blanche  (Mer de glace) consultare il sito delle Guide di CourmayeurL:  http://www.guidecourmayeur.com/index.php    Comunque in loco, attraverso i contatti del coordinatore, c’è la possibilità di trovare soluzioni più economiche.', 'MONTE BIANCO SKI', '590 €', 3, 'http://www.viaggiavventurenelmondo.it/nuovosito/viaggi/schedeviaggi/6725.php', 7141);
INSERT INTO tourscanner.tour (common_cash_description, common_cash_included_services, duration, included_services, itinerary, nation_id, not_included_services, title, price, company_id, link_to_tour, id) VALUES ('', '', '8 giorni', '7 pernottamenti con 1° colazione (facoltativo pranzo e buffet 10 €, cena convenzionata ristorante 20 €).Pacchetto immersioni comprendente 10 immersioni, guida sub, passaggio barca, bombola in aria, pesi.Servizi extraNoleggio attrezzatura (attrezzatura completa 20 € per immersione), ricariche con miscela personalizzate, Nitrox fino al 40% (3 €), corsi subacquei.Isola di Othonoi (Grecia): immersioni / snorkeling su due relitti con possibilità di avvistamento delfini durante la navigazione (250 € per subacquei - 200 € per i non subacquei)Per i non subacquei è possibile organizzare: 2 differenti uscite snorkeling guidate da biologo marino per poter osservare la tipica fauna/flora del Mar Adriatico/Ionio. Uscita nord - uscita sud: costo a persona per escursione 25 € /comprensivo di noleggio: pinne, maschera snorkel, muta.Volo per Brindisi e Bari su richiesta.Possibilità di transfer da/per aeroporto sia di Brindisi che di Bari', 'La costa del Salento sia sul versante jonico sia su quello adriatico offre una scenografia ideale per le immersioni, ricchezza di pesci di ogni tipo, relitti di navi ed aerei affondati, un gran numero di grotte affioranti o in profondità.', 1, '', 'SALENTO DIVING E OLTRE', '620 €', 3, 'http://www.viaggiavventurenelmondo.it/nuovosito/viaggi/schedeviaggi/8256.php', 7142);
