INSERT INTO tourscanner.tour (common_cash_description, common_cash_included_services, duration, included_services, itinerary, nation_id, not_included_services, title, price, company_id, link_to_tour, id) VALUES ('1.500 €', '', '23 giorni', 'il trasporto aereo dall’Italia a Maun (via Joburg) e da Kilimanjaro Airport (Tanzania) all’Italia (supplemento voli nazionali se necessari), il quaderno di viaggio Africa australe, la polizza infortuni e l’assistenza completa Europassistance (prendere visione delle condizioni delle due polizze), il trasporto in pulmino  e /o  4x4 con autista compreso il carburante per l''itinerario specificato da Maun a Kasane, Victoria Falls, Livingstone  South Luangwa NP al confine Tanzaniano, Mbeya, Parco Ruaha, i grandi parchi del Nord Tanzania, Aeroporto di Kilimanjaro.', 'Una grande spedizione via terra dal Botswana alla Tanzania attraverso i più bei parchi africani. Dalle foreste del Delta dell’Okawango alle immense savane dell’altopiano tanzaniano di Seronera e Ngorongoro, dalle Cascate Vittoria al Lago Vittoria,  due viaggi in uno per conoscere due straordinari aspetti naturalistici dell''Africa.', 3853, 'I game drive e l’ingresso ai parchi,  escursione in barca sul fiume zambezi da pagare con cassa comune, i pasti, i pernottamenti e  gli eventuali supplementi per carburante e per controlli di sicurezza applicati dai vettori aerei successivamente alla pubblicazione dei programmi e tutto ciò che non è compreso ne LA QUOTA COMPRENDE', 'THE BEST OF AFRICA', '2740 €', 3, 'http://www.viaggiavventurenelmondo.it/nuovosito/viaggi/schedeviaggi/2410.php', 7440);
